mapper.update:
UPDATE accommodation
SET overview = #{overview}
WHERE content_id = #{contentId} AND content_type_id = '32'

mapper.insert:
INSERT INTO accommodationroom (
    contentid, contenttypeid, roomtitle, roomcode, roomoffseasonminfee1,
    roomimg1, roomimg1alt, roomimg2, roomimg2alt, roomimg3, roomimg3alt,
    roomimg4, roomimg4alt, roomimg5, roomimg5alt
) VALUES (
    #{contentid}, #{contenttypeid}, #{roomtitle}, #{roomcode}, #{roomoffseasonminfee1},
    #{roomimg1}, #{roomimg1alt}, #{roomimg2}, #{roomimg2alt}, #{roomimg3},
    #{roomimg3alt}, #{roomimg4}, #{roomimg4alt}, #{roomimg5}, #{roomimg5alt}
)

mapper.select:
SELECT *
FROM accommodation
WHERE area_code = #{areaCode}

    AND cat3 = #{cat3}

LIMIT #{limit} OFFSET #{offset}
