mapper.insert:
INSERT IGNORE INTO products (
    addr1, addr2, areacode, booktour, cat1, cat2, cat3, contentid, contenttypeid, 
    createdtime, firstimage, firstimage2, cpyrhtDivCd, mapx, mapy, mlevel, 
    modifiedtime, sigungucode, tel, title, zipcode
) VALUES (
    #{addr1}, #{addr2}, #{areacode}, #{booktour}, #{cat1}, #{cat2}, #{cat3}, 
    #{contentid}, #{contenttypeid}, #{createdtime}, #{firstimage}, #{firstimage2}, 
    #{cpyrhtDivCd}, #{mapx}, #{mapy}, #{mlevel}, #{modifiedtime}, #{sigungucode}, 
    #{tel}, #{title}, #{zipcode}
);

mapper.update:
update products
	set areaname = #{areaName}, sigunguname = #{sigunguName}
	where contentid = #{contentid}
